


export function isJson(text) {
    if (text.length < 30) {
        return false
    }

    if (!text.trim().startsWith('{') && !text.trim().startsWith('\\{')) {
        return false
    }

    try {
        JSON.parse(text)
    } catch (e) {
        return false
    }
    return true
}

export function isYaml(text) {
    if (text.length < 30) {
        return false
    }

    if (
        text.search(': ') === -1 ||
        text.split('\n').length <= 1 ||
        isJson(text)
    ) {
        return false
    }

    try {
        YAML.parse(text)
    } catch (e) {
        return false
    }
    return true
}